if(document.getElementById('arr_region')!=null)
{
function array_region() {
    var options = document.getElementById('arr_region').options;
    let my_array = [];
    for (let i = 0; i < options.length; i++) {
        my_array.push(options[i].value.substring(0,3));
    }
    return my_array;
}
function array_report() {
    var options = document.getElementById('arr_report').options;
    let my_array = [];
    for (let i = 0; i < options.length; i++) {

        my_array.push(parseInt(options[i].value));
    }

    return my_array;
}
}else
{

  function array_region() {
      return "";
  }
  function array_report() {
      return '';
  }

}

